package main

import (
	"fmt"
	"sort"
)

func threeSum(nums []int) [][]int {
	res := [][]int{}
	length := len(nums)
	if length < 3 {
		return res
	}
	sort.Ints(nums)

	for idx, end := 0, length-2; idx < end; idx++ {
		if idx > 0 && nums[idx] == nums[idx-1] {
			continue
		}
		if nums[idx] > 0 {
			break
		}
		target := -nums[idx]
		left := idx + 1
		right := length - 1
		for left < right {
			sum := nums[left] + nums[right]
			if sum == target {
				tri := []int{nums[idx], nums[left], nums[right]}
				res = append(res, tri)
				if nums[left] == nums[right] {
					break
				}

				for left < right && nums[left] == tri[1] {
					left++
				}
				for right > left && nums[right] == tri[2] {
					right--
				}
			} else if sum < target {
				left++
			} else {
				right--
			}
		}
	}
	return res
}

func threeSum1(nums []int) [][]int {
	n := len(nums)
	if n < 3 {
		return [][]int{}
	}

	var res [][]int
	sort.Ints(nums)
	for i, first := range nums[:n-2] {
		if i > 0 && first == nums[i-1] {
			continue
		}
		sec, last := i+1, n-1;
		for sec < last {
			sum := first + nums[sec] + nums[last];
			if sum == 0 {
				res = append(res, []int{first, nums[sec], nums[last]})
				sec, last = sec+1, last-1
				for sec < last && nums[sec] == nums[sec-1] {
					sec++
				}

				for sec < last && nums[last] == nums[last+1] {
					last--
				}
			} else if sum < 0 {
				sec++
			} else {
				last--
			}
		}
	}
	return res
}

